			lastSubflowStateWithInput = JSON.parse(lastSubflowStateWithInput);

			const destination = setDestination({
				stateId: subflow.id,
				stateName:  subflow.name,
				subflowStateName: lastSubflowStateWithInput.name,
				subflowStateId: lastSubflowStateWithInput.id
			});

			return redirect(origin, destination, { skipCallerMessageSave: true }); 
		}
	}

	// Handle input exception on parent flow
	lastStateWithInput = JSON.parse(lastStateWithInput);

	const destination = setDestination({
		service: 'Parent Flow',
		stateId: lastStateWithInput.id
	});

	return redirect(origin, destination);
}

