function run() {
	const scripts = [ 
		processUserInput, 
		enableSubflowMode,
		disableSubflowMode,
		convertBoolToTextFlag,
		getFormattedDate,
		updateSession,
		verifyEntrypoints,
		redirect,
		getOrigin
	];

	return scripts.join('\n');
}

const processUserInput = `
	const UNEXPECTED_INPUT = 'Input inesperado';
	const INACTIVITY_FLAG = 'Inatividade';
	const ERROR_FLAG = 'Error';

	function processUserInput(input, optionsRegex) {

		try {
			const match = matchKey(optionsRegex, input);

			const selection = optionsRegex[match] || UNEXPECTED_INPUT;

			let tracking = INACTIVITY_FLAG;
			let userInput = INACTIVITY_FLAG;

			if (input) {
				// Remove accents
				tracking = selection.normalize('NFD').replace(/[\u0300-\u036f]/g, "");
				userInput = input;
			}

			return setUserInput(userInput, selection, tracking);
		} catch (e) {
			return { 
				...setUserInput(input, UNEXPECTED_INPUT, ERROR_FLAG), 
				'error': e.message 
			};
		}
	}

	function setUserInput(inputContent, selection, tracking) {
		return {
			'isValid': selection !== UNEXPECTED_INPUT,
			'content': inputContent,
			'selection': selection,
			'tracking': tracking
		}
	}

	function matchKey(options, input) {
		return Object.keys(options).find(key => (
			input.match(new RegExp(key, 'gi'))
		));
	}
`;

const enableSubflowMode = `
	function enableSubflowMode(stateId, stateName) {
		return { 'isActive': true, 'id': stateId, 'name': stateName };
	}
`;

const disableSubflowMode = `
	function disableSubflowMode(stateId, stateName) {
		return { 'isActive': false, 'id': stateId, 'name': stateName };
	}
`;

const convertBoolToTextFlag =  `
	function convertBoolToTextFlag(bool) {
		return bool ? 'Sim': 'Nao';
	}
`;

const getFormattedDate = `
	function getFormattedDate() {
		const now = new Date();
		now.setHours(now.getHours());

		let day = now.getDate();
		let month = now.getMonth() + 1;
		let year = now.getFullYear();
		let hour = now.getHours();
		let minutes = now.getMinutes();

		if (day < 10) day = '0' + day;
		if (month < 10) month = '0' + month;
		if (hour < 10) hour = '0' + hour;
		if (minutes < 10) minutes = '0' + minutes;

		return day + '/' + month + '/' + year + ' ' + hour + ':' + minutes;
	}
`;

const updateSession = `
	function updateSession(session, entrypoint, inputContent, origin) {
		try {
			session = createOrUpdateSession(session);

			session.count = parseInt(session.count) + 1;
			session.source = 'Orgânico';
			session.group = 'Orgânico';
			session.input = inputContent;
			session.origin = origin;
			session.entrypoint = false;
			session.date = getFormattedDate();

			if (entrypoint && entrypoint !== 'default') {
				entrypoint = JSON.parse(entrypoint);

				if (entrypoint.matched === 'Sim') {
					session.source = entrypoint.matchContent.source;
					session.group = entrypoint.matchContent.group;
					session.entrypoint = true;
				}
			}

			return session;
		}
		catch(e) {
			session.error = e.message;
			return session;
		}
	}

	function createOrUpdateSession(session) {
		return !session ? createSession() : JSON.parse(session);
	}

	function createSession() {
		return {
			'count': 0,
			'source': '',
			'input': '',
			'origin': '',
			'entrypoint': false,
			'date': ''
		}
	}
`

const verifyEntrypoints = `
	function verifyEntrypoints(input, entrypoints) {
		try {
			entrypoints = JSON.parse(entrypoints);

			const entrypoint = matchRegex(entrypoints, input);

			if (!entrypoint) 
				return 'default';

			return {
				'matched': 'Sim',
				'matchContent': entrypoint
			};	
		}
		catch(e) {
			return 'default';
		}
	}

	function matchRegex(entrypoints, input) {
		return entrypoints.find(e => input.match(new RegExp(e.regex.Pattern, 'gmi')));
	}
`

const redirect = `
	function setOrigin({ 
		service = 'default',
		serviceId = 'default',
		stateId = 'default', 
		stateName = 'default'
	} = {}) {
		return {
			'service': service,
			'serviceId': serviceId,
			'stateId': stateId,
			'stateName': stateName
		}
	}

	function setDestination({
		stateId,
		stateName = 'default',
		subflowStateName = 'default', 
		subflowStateId = 'default' 
	} = {}) {
		if (!stateId) 
			return 'stateId is required for redirect destination.';

		return {
			'stateId': stateId,
			'stateName': stateName,
			'subflowStateId': subflowStateId,
			'subflowStateName': subflowStateName
		}
	}

	function isModuleResponse(message, currentStateId) {
		try  {
			if (!message) return false;
			message = JSON.parse(message);
			return message.isModuleResponse && (message.to.stateId === currentStateId || message.to.subflowStateId === currentStateId);
		}
		catch(e) {
			return e.message
		}
	}

	function redirect(from, to, { skipCallerMessageSave = false, isModuleResponse = false } = {}) {
		try {
            if (typeof to === 'string') throw new Error(to);

			const message = { from: setOrigin(from) };

			if (to) message.to = to;

			message.skipCallerMessageSave = skipCallerMessageSave;
			message.isModuleResponse = isModuleResponse;

			return message;
		}
		catch(e) {
			return {
				'from': setOrigin(from),
				'to': setDestination({ 
					stateName: 'Redirect Error', 
					stateId: '7f2feee4-c5b0-4d7c-b77a-91f6ce6a771b' 
				}),
				'error': e.message,
			};
		}
	}
`

const getOrigin = `
	function getOrigin(redirect, statePreviousName) {
		const DEFAULT_ORIGIN =  'Parent Flow' + ' | ' + statePreviousName;

		try {
			if (redirect && redirect !== 'default') {
				redirect = JSON.parse(redirect);
				return redirect.from.service + ' | ' + redirect.from.stateName
			}

			return DEFAULT_ORIGIN;
		}
		catch(e) {
			return 'Error: ' + e.message;
		}
	}
`

