function run(subflowIds, inputContent) {
	subflowIds = JSON.parse(subflowIds);

	const intentsRegex = {
		'^(quit)$|(quit|end|finish)\\s*conversation': 'Quit',
		'^(attendant)$|talk\\s*to\\s*(attendant|human|agent)': 'Attendant'
	}

	const destinations = {
		'Quit': { 
			'stateId': subflowIds.Finalization 
		},
		'Attendant': {
			'stateId': subflowIds.Desk
		}
	}

	const key = intentMatch(intentsRegex, normalizeInput(inputContent));

	const intent = intentsRegex[key];

	const destination = destinations[intent];

	return destination ? setDestination(destination) : 'default';
} 

function intentMatch(intents, input) {
	return Object.keys(intents).find(key => input.match(new RegExp(key, 'gmi')))
}

function normalizeInput(text) {
  return text.normalize('NFD').replace(/[\u0300-\u036f]/g, '')
}

