function run(
	service,
	entrypoint,
	subflowIds,
	exceptionMessage,
	destination
) {
	const origin = setOrigin({
		service: service,
		stateId: '{{state.id}}',
		stateName: '{{state.name}}'
	});

	// Handle entrypoint redirect
	if (entrypoint && entrypoint !== 'default') {
		subflowIds = subflowIds && JSON.parse(subflowIds);
		const destination = setDestination({ stateId: subflowIds.Onboarding });
		return redirect(origin, destination);
	}

	// Handle global regex redirect
	if (destination && destination !== 'default') {
		destination = JSON.parse(destination);
		return redirect(origin, destination);
	}

	// If input does not match Entrypoint or Global Regex, then return to last state with input.
	exceptionMessage = JSON.parse(exceptionMessage);
	return exceptionMessage;
}

function run(subflow, lastStateWithInput, lastSubflowStateWithInput) {
