			return {
				"flowId": subflow.id,
				"stateId": lastSubflowStateWithInput.id
			}
		}

		lastStateWithInput = JSON.parse(lastStateWithInput);

		return {
			"flowId": "default",
			"stateId": lastStateWithInput.id
		}
	}
	catch(e) {
		return {
			"flowId": "default",
			"stateId": "default",
			"error": e.message
		}
	}
}

function run(errorCount, currentExceptionOrigin, previousExceptionOrigin, maxErrorCount) {
    try {
        if (!errorCount || errorCount === "NaN" || parseInt(errorCount) >= maxErrorCount) {
            errorCount = 0;
        }

        currentExceptionOrigin = JSON.parse(currentExceptionOrigin);
        previousExceptionOrigin = JSON.parse(previousExceptionOrigin);

        if (shouldIncrementErrorCount(currentExceptionOrigin, previousExceptionOrigin)) 
        {
            return parseInt(errorCount) + 1;    
        }

